import { TODO_ADD, TODO_TOGGLE, TODO_DELETE, TODO_VISIBILITY, TODO_CLEAR } from '../constant/todo'

const initial_state = {
  list: [],
  filter: 'All'
}

function reducer (state = initial_state, action) {
  const { payload, type } = action
  const { list } = state
  switch (type) {
    case TODO_ADD: {
      const todo_list = list.concat(payload)
      return {
        ...state,
        list: todo_list
      }
    }
    case TODO_TOGGLE: {
      const todo_list = list.map(todo => {
        if(todo.id === payload) return {...todo, completed: !todo.completed}
        return todo
      })
      return {
        ...state,
        list: todo_list
      }
    }
    case TODO_DELETE: {
      const todo_list = list.filter(todo => todo.id !== payload)
      return {
        ...state,
        list: todo_list
      }
    }
    case TODO_VISIBILITY: {
      return {
        ...state,
        filter: payload
      }
    }
    case TODO_CLEAR: {
      return {
        ...state,
        list: []
      }
    }
    default:
      return state
  }
}

export default reducer
