import { TODO_ADD, TODO_TOGGLE, TODO_DELETE, TODO_VISIBILITY } from '../constant/todo'

function todo_reducer (state = [], action) {
  const { payload, type } = action
  switch (type) {
    case TODO_ADD: 
      return [...state, payload]
    case TODO_TOGGLE:
      return state.map(todo => {
        if(todo.id === payload) return {...todo, completed: !todo.completed}
        return todo
      })
    case TODO_DELETE:
      const copy_todos = [...state]
      const todo = copy_todos.find(todo => todo.id === payload)
      if(todo) return copy_todos
      return state
    default:
      return state
  }
}

function visibility_reducer(state = 'All', action) {
  const { type, payload } = action
  switch(type) {
    case TODO_VISIBILITY:
      return payload
    default:
      return state
  }
}

export {
  visibility_reducer,
  todo_reducer
}
