import { memo } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import styles from '../index.module.scss'

const filters = ['All', 'Active', 'Completed']


const Filter = memo(() => {
  const unfinished_todo_length = useSelector(state => state.todos.list).filter(todo => !todo.completed).length
  const filter = useSelector(state => state.todos.filter)

  return (
    <div className={styles.filter_wrapper}>
      <span>{unfinished_todo_length > 0 ? unfinished_todo_length : 'No' } items left</span>
      <span>
      {
        filters.map(f => (
          <i key={f} className={[styles.filter_item, f === filter ? 'active' : ''].join('')}>{f}</i>
        ))
      }
      </span>
      <span>Clear Completed</span>
    </div>
  )
})

export default Filter
