import styles from './index.module.scss'
import { memo, useState } from 'react'
import { Input, Button, Empty, message } from 'antd'
import Todo from './components/Todo'
import { useSelector, useDispatch } from 'react-redux'
import { add_todo } from '@/store/action/todo'
import Filter from './components/Filter'

const size = 'large'

const TodoList = memo(() => {
  const [ todo, setTodo ] = useState('')
  const handleTodoChanged = (event) => {
    setTodo(event.target.value)
  }
  const todo_list = useSelector(state => state.todos.list)
  const dispatch = useDispatch()
  const handleConfirm = () => { // 添加todo
    if(!todo.trim()) {
      message.warning('todo is required')
      return
    }
    dispatch(add_todo({
      text: todo.trim(),
      id: Date.now(),
      completed: false
    }))
    setTodo('')
  }
  return (
    <div className={styles.wrapper}>
      <div className={styles.header}>
        <Input
          placeholder='What next to be done?'
          value={todo} onChange={handleTodoChanged}
          className={styles.input}
          size={size}
        />
        <Button type='primary' size={size} onClick={handleConfirm}>Confirm</Button>
      </div>
      <div className={styles.list}>
        {
          todo_list.length > 0 ? (todo_list.map(todo => (
            <Todo key={todo.id} {...todo}/>
          ))) : <Empty image={Empty.PRESENTED_IMAGE_SIMPLE}/>
        }
      </div>
      <Filter/>
    </div>
  )
})

export default TodoList
